function calculateSU(e){var n=Math.floor(e/16);return"ArtNet subnet: "+n+"<br>ArtNet universe: "+(e-16*n)}function renderFixtAnswer(e,n){n.html("<strong>"+calculateSU(e)+"</strong>")}function calculateFixtUni(e){var n=16*e[0];return n+=1*e[1]}function renderSUAnswer(e,n){n.html("Fixture universe: <strong>"+calculateFixtUni(e)+"</strong>")}$("#fixture-form").submit(function(){event.preventDefault();var e=$("#fixtUni").val();$("#fixt-answer").show(),renderFixtAnswer(e,$("#fixt-answer"))}),$("#console-form").submit(function(){event.preventDefault();var e=[];e.push($("#sub").val()),e.push($("#uni").val()),$("#console-answer").show(),renderSUAnswer(e,$("#console-answer"))});